import React, { useState, useEffect } from 'react';
import { ref, onValue, off } from 'firebase/database';
import { database } from '../firebase';

function History() {
  const [readings, setReadings] = useState([]);
  const [sources, setSources] = useState([]);
  const [selectedSource, setSelectedSource] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const readingsRef = ref(database, 'readings');
    
    const handleData = (snapshot) => {
      try {
        const data = snapshot.val();
        if (data) {
          const readingsArray = Object.entries(data).map(([id, reading]) => ({
            id,
            ...reading
          }));
          setReadings(readingsArray.reverse()); // Most recent first

          // Extract unique sources
          const uniqueSources = [...new Set(readingsArray.map(r => r.source))];
          setSources(uniqueSources);
        } else {
          setReadings([]);
          setSources([]);
        }
        setError('');
      } catch (err) {
        setError('Error processing data from Firebase');
        console.error('Error processing data:', err);
      } finally {
        setLoading(false);
      }
    };

    const handleError = (error) => {
      setError('Failed to fetch data from Firebase');
      console.error('Firebase error:', error);
      setLoading(false);
    };

    // Set up the listener
    onValue(readingsRef, handleData, handleError);

    // Cleanup function
    return () => {
      off(readingsRef);
    };
  }, []);

  const filteredReadings = selectedSource === 'all'
    ? readings
    : readings.filter(reading => reading.source === selectedSource);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[60vh]">
        <div className="card w-96 bg-base-100 shadow-xl">
          <div className="card-body items-center text-center">
            <h2 className="card-title">Loading History</h2>
            <div className="flex justify-center">
              <span className="loading loading-spinner loading-lg text-primary"></span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="card bg-base-100 shadow-xl">
        <div className="card-body">
          <h2 className="card-title">Historical Readings</h2>
          
          {error && (
            <div className="alert alert-error">
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{error}</span>
            </div>
          )}

          <div className="form-control w-full max-w-xs">
            <label className="label">
              <span className="label-text">Filter by source</span>
            </label>
            <select 
              className="select select-bordered"
              value={selectedSource}
              onChange={(e) => setSelectedSource(e.target.value)}
            >
              <option value="all">All Sources</option>
              {sources.map(source => (
                <option key={source} value={source}>{source}</option>
              ))}
            </select>
          </div>

          {filteredReadings.length === 0 ? (
            <div className="alert alert-info">
              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" className="stroke-current shrink-0 w-6 h-6">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <span>No readings found</span>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>Source</th>
                    <th>Time</th>
                    <th>Temperature</th>
                    <th>TDS</th>
                    <th>DO</th>
                    <th>Turbidity</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredReadings.map(reading => (
                    <tr key={reading.id} className="hover">
                      <td>{reading.source}</td>
                      <td>{new Date(reading.timestamp).toLocaleString()}</td>
                      <td>{reading.temp}Â°C</td>
                      <td>{reading.tds} ppm</td>
                      <td>{reading.do} mg/L</td>
                      <td>{reading.ntu} NTU</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default History;
