import React from 'react';
import { motion } from 'framer-motion';
import { useLocation } from 'react-router-dom';

// Map routes to their index for determining slide direction
const routeIndex = {
  '/': 0,
  '/history': 1,
  '/about': 2
};

const PageTransition = ({ children }) => {
  const location = useLocation();
  const currentIndex = routeIndex[location.pathname];
  const prevIndex = React.useRef(currentIndex);

  React.useEffect(() => {
    prevIndex.current = currentIndex;
  }, [currentIndex]);

  const slideDirection = currentIndex > prevIndex.current ? 1 : -1;

  return (
    <motion.div
      key={location.pathname}
      initial={{ x: `${100 * slideDirection}%`, opacity: 0 }}
      animate={{ x: 0, opacity: 1 }}
      exit={{ x: `${-100 * slideDirection}%`, opacity: 0 }}
      transition={{
        type: "spring",
        stiffness: 300,
        damping: 30,
        duration: 0.2
      }}
      style={{
        width: '100%',
        height: '100%',
        position: 'absolute'
      }}
    >
      {children}
    </motion.div>
  );
};

export default PageTransition;
