import { createGlobalStyle } from 'styled-components';
import { theme } from './theme';

export const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html {
    font-size: 16px;
    height: 100%;
  }

  body {
    font-family: ${theme.typography.fontFamily};
    background-color: ${theme.colors.background};
    color: ${theme.colors.text.primary};
    line-height: 1.6;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility;
    height: 100%;
    font-feature-settings: "kern" 1, "liga" 1, "calt" 1, "ss01" 1, "ss02" 1, "ss03" 1;
  }

  #root {
    height: 100%;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: ${theme.typography.fontFamily};
    font-weight: 700;
    margin-bottom: ${theme.spacing.md};
    color: ${theme.colors.text.primary};
    letter-spacing: -0.01em;
  }

  h1 {
    font-size: ${theme.typography.h1.fontSize};
    line-height: ${theme.typography.h1.lineHeight};
    font-weight: ${theme.typography.h1.fontWeight};
    letter-spacing: ${theme.typography.h1.letterSpacing};
  }

  h2 {
    font-size: ${theme.typography.h2.fontSize};
    line-height: ${theme.typography.h2.lineHeight};
    font-weight: ${theme.typography.h2.fontWeight};
    letter-spacing: ${theme.typography.h2.letterSpacing};
  }

  h3 {
    font-size: ${theme.typography.h3.fontSize};
    line-height: ${theme.typography.h3.lineHeight};
    font-weight: ${theme.typography.h3.fontWeight};
    letter-spacing: ${theme.typography.h3.letterSpacing};
  }

  p {
    margin-bottom: ${theme.spacing.md};
    font-size: ${theme.typography.body1.fontSize};
    line-height: ${theme.typography.body1.lineHeight};
    font-weight: ${theme.typography.body1.fontWeight};
    letter-spacing: ${theme.typography.body1.letterSpacing};
  }

  button {
    cursor: pointer;
    font-family: ${theme.typography.fontFamily};
    border: none;
    outline: none;
    font-weight: 600;
  }

  input {
    font-family: ${theme.typography.fontFamily};
    outline: none;
    font-size: ${theme.typography.body1.fontSize};
    font-weight: ${theme.typography.body1.fontWeight};
  }

  strong {
    font-weight: 700;
    color: ${theme.colors.text.primary};
  }

  /* Mobile-first responsive design */
  @media (max-width: ${theme.breakpoints.mobile}) {
    html {
      font-size: 14px;
    }
  }

  /* Tablet */
  @media (min-width: ${theme.breakpoints.tablet}) {
    html {
      font-size: 15px;
    }
  }

  /* Desktop */
  @media (min-width: ${theme.breakpoints.desktop}) {
    html {
      font-size: 16px;
    }
  }
`;
