import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';

const Nav = styled.nav`
  background-color: ${props => props.theme.colors.surface};
  padding: ${props => props.theme.spacing.md} ${props => props.theme.spacing.lg};
  box-shadow: ${props => props.theme.shadows.sm};
  position: sticky;
  top: 0;
  z-index: 100;
`;

const NavContainer = styled.div`
  max-width: ${props => props.theme.breakpoints.wide};
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled(Link)`
  display: flex;
  align-items: center;
  text-decoration: none;
  color: ${props => props.theme.colors.text.primary};
  font-weight: 700;
  font-size: ${props => props.theme.typography.h3.fontSize};
`;

const LogoImage = styled.img`
  height: 32px;
  margin-right: ${props => props.theme.spacing.sm};
`;

const NavList = styled.ul`
  display: flex;
  list-style: none;
  gap: ${props => props.theme.spacing.md};
  margin: 0;
  padding: 0;

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    display: ${props => props.isOpen ? 'flex' : 'none'};
    flex-direction: column;
    position: absolute;
    top: 60px;
    right: 0;
    background-color: ${props => props.theme.colors.surface};
    padding: ${props => props.theme.spacing.md};
    border-radius: ${props => props.theme.borderRadius.md};
    box-shadow: ${props => props.theme.shadows.md};
  }
`;

const NavItem = styled.li`
  margin: 0;
`;

const NavLink = styled(Link)`
  text-decoration: none;
  color: ${props => props.isActive ? props.theme.colors.primary : props.theme.colors.text.primary};
  font-weight: ${props => props.isActive ? 600 : 500};
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border-radius: ${props => props.theme.borderRadius.md};
  transition: all 0.2s;

  &:hover {
    color: ${props => props.theme.colors.primary};
    background-color: ${props => props.theme.colors.background};
  }
`;

const MenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  cursor: pointer;
  padding: ${props => props.theme.spacing.sm};
  color: ${props => props.theme.colors.text.primary};

  @media (max-width: ${props => props.theme.breakpoints.tablet}) {
    display: block;
  }
`;

const AuthButton = styled.button`
  background-color: ${props => props.theme.colors.primary};
  color: white;
  border: none;
  padding: ${props => props.theme.spacing.sm} ${props => props.theme.spacing.md};
  border-radius: ${props => props.theme.borderRadius.md};
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: ${props => props.theme.colors.primaryDark};
  }
`;

function Navbar() {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const { currentUser, logout } = useAuth();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Failed to log out:', error);
    }
  };

  return (
    <Nav>
      <NavContainer>
        <Logo to="/">
          <LogoImage src="/logo.svg" alt="Water Quality Monitor" />
          Water Quality Monitor
        </Logo>
        <MenuButton onClick={() => setIsOpen(!isOpen)}>
          ☰
        </MenuButton>
        <NavList isOpen={isOpen}>
          {currentUser ? (
            <>
              <NavItem>
                <NavLink to="/" isActive={location.pathname === '/'}>
                  Dashboard
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to="/history" isActive={location.pathname === '/history'}>
                  History
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to="/about" isActive={location.pathname === '/about'}>
                  About
                </NavLink>
              </NavItem>
              <NavItem>
                <AuthButton onClick={handleLogout}>Logout</AuthButton>
              </NavItem>
            </>
          ) : (
            <>
              <NavItem>
                <NavLink to="/about" isActive={location.pathname === '/about'}>
                  About
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to="/login" isActive={location.pathname === '/login'}>
                  Login
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to="/signup" isActive={location.pathname === '/signup'}>
                  Sign Up
                </NavLink>
              </NavItem>
            </>
          )}
        </NavList>
      </NavContainer>
    </Nav>
  );
}

export default Navbar;
