import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { ref, push } from 'firebase/database';
import { database } from '../firebase';

const DashboardContainer = styled.div`
  padding: ${({ theme }) => theme.spacing.md};
  max-width: 1200px;
  margin: 0 auto;
`;

const StatusBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  padding: ${({ theme }) => theme.spacing.md};
  background-color: ${({ theme, isConnected }) => 
    isConnected ? theme.colors.success.light : theme.colors.error.light};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  color: ${({ theme, isConnected }) => 
    isConnected ? theme.colors.success.dark : theme.colors.error.dark};
`;

const SensorGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${({ theme }) => theme.spacing.md};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
`;

const SensorCard = styled.div`
  background-color: ${({ theme }) => theme.colors.surface};
  padding: ${({ theme }) => theme.spacing.lg};
  border-radius: ${({ theme }) => theme.borderRadius.lg};
  box-shadow: ${({ theme }) => theme.shadows.sm};
  transition: transform 0.2s ease-in-out;

  &:hover {
    transform: translateY(-4px);
    box-shadow: ${({ theme }) => theme.shadows.md};
  }
`;

const SensorValue = styled.div`
  font-size: 2.5rem;
  font-weight: bold;
  color: ${({ theme }) => theme.colors.primary.main};
  margin: ${({ theme }) => theme.spacing.sm} 0;
`;

const SensorLabel = styled.div`
  font-size: 1rem;
  color: ${({ theme }) => theme.colors.text.secondary};
  text-transform: uppercase;
  letter-spacing: 1px;
`;

const SensorUnit = styled.div`
  font-size: 0.875rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const SaveButton = styled.button`
  background-color: ${({ theme }) => theme.colors.primary.main};
  color: white;
  border: none;
  padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.lg};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.2s ease-in-out;
  width: 100%;
  max-width: 300px;
  margin: 0 auto;
  display: block;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primary.dark};
  }

  &:disabled {
    background-color: ${({ theme }) => theme.colors.text.disabled};
    cursor: not-allowed;
  }
`;

const SourceInput = styled.input`
  width: 100%;
  max-width: 300px;
  padding: ${({ theme }) => theme.spacing.md};
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  font-size: 1rem;
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  display: block;
  margin: 0 auto ${({ theme }) => theme.spacing.lg};

  &:focus {
    outline: none;
    border-color: ${({ theme }) => theme.colors.primary.main};
  }
`;

const LoadingSpinner = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  font-size: 1.5rem;
  color: ${({ theme }) => theme.colors.text.secondary};
`;

const ErrorMessage = styled.div`
  background-color: ${({ theme }) => theme.colors.error.light};
  color: ${({ theme }) => theme.colors.error.dark};
  padding: ${({ theme }) => theme.spacing.md};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  margin-bottom: ${({ theme }) => theme.spacing.lg};
  text-align: center;
`;

const Dashboard = () => {
  const [sensorData, setSensorData] = useState({
    temp: 0,
    tds: 0,
    do: 0,
    ntu: 0,
    ntu_lin: 0
  });
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sourceName, setSourceName] = useState('');
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/esp32-data');
        if (!response.ok) {
          throw new Error('Failed to fetch data from ESP32');
        }
        const data = await response.json();
        setSensorData(data);
        setIsConnected(true);
        setError(null);
      } catch (err) {
        setError(err.message);
        setIsConnected(false);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);
    return () => clearInterval(interval);
  }, []);

  const handleSave = async () => {
    if (!sourceName.trim()) {
      setError('Please enter a source name');
      return;
    }

    setIsSaving(true);
    try {
      const dataToSave = {
        ...sensorData,
        sourceName,
        timestamp: new Date().toISOString()
      };
      await push(ref(database, 'readings'), dataToSave);
      setSourceName('');
      setError(null);
    } catch (err) {
      setError('Failed to save data to Firebase');
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return <LoadingSpinner>Loading sensor data...</LoadingSpinner>;
  }

  return (
    <DashboardContainer>
      <StatusBar isConnected={isConnected}>
        <div>ESP32 Status: {isConnected ? 'Connected' : 'Disconnected'}</div>
        <div>Last Update: {new Date().toLocaleTimeString()}</div>
      </StatusBar>

      {error && <ErrorMessage>{error}</ErrorMessage>}

      <SensorGrid>
        <SensorCard>
          <SensorLabel>Temperature</SensorLabel>
          <SensorValue>{sensorData.temp.toFixed(1)}</SensorValue>
          <SensorUnit>Â°C</SensorUnit>
        </SensorCard>
        <SensorCard>
          <SensorLabel>TDS</SensorLabel>
          <SensorValue>{sensorData.tds.toFixed(1)}</SensorValue>
          <SensorUnit>ppm</SensorUnit>
        </SensorCard>
        <SensorCard>
          <SensorLabel>Dissolved Oxygen</SensorLabel>
          <SensorValue>{sensorData.do.toFixed(1)}</SensorValue>
          <SensorUnit>mg/L</SensorUnit>
        </SensorCard>
        <SensorCard>
          <SensorLabel>Turbidity (NTU)</SensorLabel>
          <SensorValue>{sensorData.ntu.toFixed(1)}</SensorValue>
          <SensorUnit>NTU</SensorUnit>
        </SensorCard>
        <SensorCard>
          <SensorLabel>Turbidity (Linear)</SensorLabel>
          <SensorValue>{sensorData.ntu_lin.toFixed(1)}</SensorValue>
          <SensorUnit>NTU</SensorUnit>
        </SensorCard>
      </SensorGrid>

      <SourceInput
        type="text"
        value={sourceName}
        onChange={(e) => setSourceName(e.target.value)}
        placeholder="Enter water source name"
      />

      <SaveButton 
        onClick={handleSave} 
        disabled={isSaving || !isConnected}
      >
        {isSaving ? 'Saving...' : 'Save Current Readings'}
      </SaveButton>
    </DashboardContainer>
  );
};

export default Dashboard;
