import React, { useState, useEffect } from 'react';
import { ref, push, set } from 'firebase/database';
import { database } from '../firebase';

function Dashboard() {
  const [sensorData, setSensorData] = useState({
    temp: 0,
    tds: 0,
    do: 0,
    ntu: 0,
    ntu_lin: 0
  });
  const [source, setSource] = useState('');
  const [error, setError] = useState('');
  const [isSaving, setIsSaving] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('Connecting...');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/data');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        if (isMounted) {
          setSensorData({
            temp: parseFloat(data.temp).toFixed(2),
            tds: parseFloat(data.tds).toFixed(2),
            do: parseFloat(data.do).toFixed(2),
            ntu: parseFloat(data.ntu).toFixed(2),
            ntu_lin: parseFloat(data.ntu_lin).toFixed(2)
          });
          setError('');
          setConnectionStatus('Connected to ESP32');
          setIsLoading(false);
        }
      } catch (err) {
        if (isMounted) {
          setError('Failed to fetch sensor data. Please check ESP32 connection.');
          setConnectionStatus('Connection Error');
          setIsLoading(false);
          console.error('Error fetching data:', err);
        }
      }
    };

    fetchData();
    const interval = setInterval(fetchData, 2000);

    return () => {
      isMounted = false;
      clearInterval(interval);
    };
  }, []);

  const handleSave = async () => {
    if (!source.trim()) {
      setError('Please enter a source name');
      return;
    }

    setIsSaving(true);
    try {
      const readingsRef = ref(database, 'readings');
      const newReadingRef = push(readingsRef);
      await set(newReadingRef, {
        ...sensorData,
        source: source.trim(),
        timestamp: new Date().toISOString()
      });
      setError('');
      setSource('');
    } catch (err) {
      setError('Failed to save data to Firebase');
      console.error('Error saving data:', err);
    } finally {
      setIsSaving(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="card w-96 bg-base-100 shadow-xl">
          <div className="card-body">
            <h2 className="card-title">Loading Sensor Data...</h2>
            <div className="flex justify-center">
              <span className="loading loading-spinner loading-lg"></span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <div className="card bg-base-100 shadow-xl">
        <div className="card-body">
          <div className="flex justify-between items-center mb-4">
            <h2 className="card-title">Current Sensor Readings</h2>
            <div className={`badge ${connectionStatus === 'Connected to ESP32' ? 'badge-success' : 'badge-error'}`}>
              {connectionStatus}
            </div>
          </div>

          {error && (
            <div className="alert alert-error mb-4">
              <svg xmlns="http://www.w3.org/2000/svg" className="stroke-current shrink-0 h-6 w-6" fill="none" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
              <span>{error}</span>
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
            <div className="stats shadow">
              <div className="stat">
                <div className="stat-title">Temperature</div>
                <div className="stat-value">{sensorData.temp}°C</div>
              </div>
            </div>
            <div className="stats shadow">
              <div className="stat">
                <div className="stat-title">TDS</div>
                <div className="stat-value">{sensorData.tds} ppm</div>
              </div>
            </div>
            <div className="stats shadow">
              <div className="stat">
                <div className="stat-title">Dissolved Oxygen</div>
                <div className="stat-value">{sensorData.do} mg/L</div>
              </div>
            </div>
            <div className="stats shadow">
              <div className="stat">
                <div className="stat-title">Turbidity (Quadratic)</div>
                <div className="stat-value">{sensorData.ntu} NTU</div>
              </div>
            </div>
            <div className="stats shadow">
              <div className="stat">
                <div className="stat-title">Turbidity (Linear)</div>
                <div className="stat-value">{sensorData.ntu_lin} NTU</div>
              </div>
            </div>
          </div>

          <div className="flex flex-col md:flex-row gap-4">
            <input
              type="text"
              placeholder="Enter water source (e.g., tap, lake, well)"
              className="input input-bordered flex-1"
              value={source}
              onChange={(e) => setSource(e.target.value)}
              disabled={isSaving}
            />
            <button
              className="btn btn-primary"
              onClick={handleSave}
              disabled={isSaving}
            >
              {isSaving ? (
                <>
                  <span className="loading loading-spinner"></span>
                  Saving...
                </>
              ) : (
                'Save to Firebase'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
